
<?php 
use Carbon\Carbon;
$Query =DB::table('Contact')->select(DB::raw("'/__files/rendition/' + CONVERT(nvarchar(10), (SELECT TOP 1  dc.Document_ID
                FROM Document_Categories dc
                INNER JOIN Document_Entities de ON de.Document_ID = dc.Document_ID
                INNER JOIN Document d ON d.Document_ID = dc.Document_ID
                WHERE dc.Category_ID = 444 AND de.Entity_Class_ID = 1 AND de.Entity_Identifier = $ID
                    AND LOWER(d.File_Type) IN ('png', 'jpg', 'jpeg', 'gif', 'bmp', 'tif', 'tiff') AND d.Superceded_By IS NULL
                ORDER BY dc.Document_ID DESC)) + '/-9/photo.jpg' as Photo"))->where("Contact_ID","=",$ID)->first();

if($Query->Photo){

$Photo = "https://themis.ukht.org$Query->Photo";

}else{
	
	$Photo = "data:image/png;base64,";
}


$Absent = DB::table('Absence_Adjustment')->where("Contact_ID","=",$ID)->where('Start_At','<=',DB::raw('GETDATE()'))->where('End_At','>',DB::raw('dateadd(DD, -1, cast(GETDATE() as date))'))->exists();
$OOH = DB::table('UKHT_ICT_Work_Hours')->where("Contact_ID","=",$ID)->where('Day', '=', date("l") )->where('Start','<=',DB::raw('CONVERT (time, CURRENT_TIMESTAMP) '))->where('End','>=',DB::raw('CONVERT (time, CURRENT_TIMESTAMP) '))->exists();

if($OOH){

if($Absent){
	$Color = "red";
	$Location = "On Leave";
}else{

	
	$current_time = Carbon::now()->format('H:i');
$sunrise = $LunchStart;
$sunset = $LunchEnd;
$date1 = DateTime::createFromFormat('H:i', $current_time);
$date2 = DateTime::createFromFormat('H:i', $sunrise);
$date3 = DateTime::createFromFormat('H:i', $sunset);
if ($date1 > $date2 && $date1 < $date3)
{
  $Color = "orange";
	$Location = "on Lunch";
	
	
}else{
	
	
	
	
	
	$OOA = DB::table('UKHT_ICT_Locale')->where('Contact_ID','=',$ID)->whereDate('DATE', '=', Carbon::today()->toDateString())->first();
	
	if($OOA){
		
		$Color = "amber";
$Location = $OOA->Location;
		
	}else{
	
$Color = "green";
$Location = "In Swindon";
}
}
}

}
else{
	$Color = "black";
	$Location = "OUT OF HOURS";
	$TColor = "text-white";
}

